package com.whitemind.cogit.member.controller;import javax.servlet.http.HttpServletRequest;import org.springframework.web.bind.annotation.PatchMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import com.whitemind.cogit.common.response.ResponseResult;import com.whitemind.cogit.member.dto.UpdateMemberNicknameDto;import com.whitemind.cogit.member.entity.request.CreateStudyRequest;import com.whitemind.cogit.member.service.StudyService;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;@Slf4j@RestController@RequiredArgsConstructor@RequestMapping("/study")public class StudyController {	private final StudyService studyService;	@PostMapping("/create")	public ResponseResult createStudy(@RequestBody CreateStudyRequest study) throws Exception {		log.info(study.getStudyName());		studyService.createStudy(study.getStudyName());		return ResponseResult.successResponse;	}}